import React, { Component} from 'react';
import $ from 'jquery';
//import data from "../data"; 
import axios from 'axios';
class Api extends Component{
	constructor(props) {
    super(props);
		this.state = {
		  error: null,
		  isLoaded: false,
		  items: [],
		  categories: []
		};
    }
	componentDidMount() {
		$(document).ready(function() {
            $( ".card" ).hover(
                function() {
                    $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
                }, function() {
                    $(this).removeClass('shadow-lg');
                }
            );
        });
		/*========================================================*/
		/*
		axios.get('http://www.reddit.com/r/reactjs.json')
		  .then(res => {
			    console.log(res.data.data.children.map);
			const posts = res.data.data.children.map(obj => obj.data);
			this.setState({ posts });
		  });
		  
		  
		  axios.get('reactjs.json')
		  .then(response => {
			   console.log(response.data.jobs);
			  const posts = response.data.jobs.map(obj => obj.data);
			//console.log(posts);
			this.setState({ posts });
		  });*/
		  
	/*	  
    fetch("reactjs.json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
       
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
		  
		*/  
		 this.fetchApiList(); 
	}
	
	fetchApiList() {
     axios.get("http://www.varunshrivastava.in/wp-json/wp/v2/categories")
      .then((res) => {
       this.setState({categories: res.data.categories});
      });
    }
	
	render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {items.map(item => (
            <li key={item.title}>
              {item.title} {item.price}
            </li>
          ))}
        </ul>
      );
    }
  }
  
  

}
export default Api;
