import React, { Component } from 'react';
import axios from 'axios';
class Login extends Component { 
   constructor(props){
      super(props);
	  this.handleChange = this.onChangeUserEmail.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
	  
	   this.onChangeUserName = this.onChangeUserName.bind(this);
       //this.onChangeUserEmail = this.onChangeUserEmail.bind(this);
         this.onSubmit = this.onSubmit.bind(this);
	  
	  
	  
      this.state = {
		  email:'',
		  password:''
		  };
	   
   }
  
   //handleChange(event) {
      //this.setState({ value: event.target.value})
	//}
	//onChangeUserName(e) {
      //  this.setState({ name: e.target.value })
    //}

     nChangeUserEmail(e) {
        this.setState({ email: e.target.value })
    }
	
   onSubmit(event) {
   event.preventDefault()
   const userObject = {
      email: this.state.email,
      password: this.state.pwd
   };
   console.log(userObject);
   axios.post('http://localhost:5000/login-post', userObject)
      .then((res) => {
          console.log(res.data)
      }).catch((error) => {
          console.log(error)
      });
}
   render(){
     return (
      <div className ="container">
        <h2>Login Form</h2>
         <form onSubmit={this.onSubmit}>
            <div className="form-group">
               <label htmlFor="email">Email:</label>
              <input type="email"className ="form-control" id="email" placeholder="Enter email" name="email" value={this.state.email} onChange={this.onChangeUserEmail}/>
            </div>
            <div className="form-group">
              <label htmlFor="pwd">Password:</label>
              <input type="password" className ="form-control" id="pwd" placeholder="Enter password" name="pwd"/>
            </div>
           <button type="submit" className ="btn btn-default">Submit</button>
         </form>
      </div>
     );
    }
}


export default Login;
