import React, { Component } from 'react';
import $ from 'jquery';
import axios from 'axios';
const APIs_URL = 'final-data.json';
class APIsBox extends Component {
  state = {
    APIs : []
  }
  componentDidMount() {
    //const url = `${APIs_URL}/APIs/`;
	const url = `${APIs_URL}`;
    axios.get(url).then(response => response.data)
    .then((data) => {
      this.setState({ APIs: data })
      //console.log(this.state.APIs)
     })
	 
	 $(document).ready(function() {
            $( ".card" ).hover(
                function() {
                    $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
                }, function() {
                    $(this).removeClass('shadow-lg');
                }
            );
        });
	 
  }
    render() {

    return (
		<div className="row mb-4">{
			this.state.APIs.map((APIs, i) => (
			<div key={i}className="col-sm-6 py-2 col-lg-4">
				<div key={i}className="card h-100">
					<div key={i}className="card-body">
						<h5 key={i}className="card-title">
						<i className={APIs.icon}></i> {APIs.title}</h5>
						<p key={i} className="card-text">{APIs.descriptions}</p>
					</div>
					<div className="card-detail mx-3 "> 
					    <ul className = "list-inline ">
							<li>
							<a key={i} href={APIs.link} className="text-dark no-underline text-capitalize footer-link-icon">Intro <span className="position-relative"><i className="fa fa-chevron-right float-right" ></i></span></a>
							</li>
							<hr/>
							<li><a  key={i} href={APIs.link} className="text-dark no-underline text-capitalize">Developer's guide <span className="position-relative"><i className="fa fa-chevron-right float-right"></i></span></a></li>
							<hr/>
							<li><a  key={i} href={APIs.link} className="text-dark no-underline text-capitalize"> API reference<span className="position-relative">
							<i className="fa fa-chevron-right float-right"></i></span></a></li>
						</ul>
					</div>
				</div>
			 </div>
			))
		}
        
       </div>
    );
  }
}

export default APIsBox;
