import React, { Component } from 'react';
import axios from 'axios';
const API_URL = 'final-data.json';

class ApiBox extends Component {
  state = {
    users: []
  }
  componentDidMount() {
    //const url = `${API_URL}/users/`;
	const url = `${API_URL}`;
    axios.get(url).then(response => response.data)
    .then((data) => {
      this.setState({ users: data })
      //console.log(this.state.users)
     })
  }
    render() {

    return (
       <div className="container">
        {
			this.state.users.map((user, i) => (
		<div className="col-sm-6 py-2 col-lg-4">
          <div key={i} className="card h-100">
           <div key={i} className="card-body">
				<h5 className="card-title"><i class={user.icon}></i>{user.title}</h5>
			   <p className="card-text">{user.descriptions}</p>
              <h6 className="card-subtitle mb-2 text-muted">
              {user.email}             
              </h6>
            </div>
          </div>
		  <div className="card-detail mx-3 "> 
		  <ul class = "list-inline ">
							<li><a  href={user.link} class="text-dark no-underline text-capitalize footer-link-icon">Intro <span class="position-relative"><i class="fa fa-chevron-right float-right" ></i></span></a></li>
							<hr/>
							<li><a  href={user.link} class="text-dark no-underline text-capitalize">Developer's guide <span class="position-relative"><i class="fa fa-chevron-right float-right"></i></span></a></li>
							<hr/>
							<li><a   href={user.link} class="text-dark no-underline text-capitalize"> API reference<span class="position-relative"><i class="fa fa-chevron-right float-right"></i></span></a></li>
						</ul>
		  </div>
		  </div>
		  
        ))}
        
       </div>
    );
  }
}

export default ApiBox;
