import React, { Component } from 'react';
import $ from 'jquery';
import axios from 'axios';
const APIs_URL = 'final-data.json';
class APIsBox extends Component {
  state = {
    APIs : []
  }
  componentDidMount() {
    //const url = `${APIs_URL}/APIs/`;
	const url = `${APIs_URL}`;
    axios.get(url).then(response => response.data)
    .then((data) => {
      this.setState({ APIs: data })
      //console.log(this.state.APIs)
     })
	 
	 $(document).ready(function() {
            $( ".card" ).hover(
                function() {
                    $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
                }, function() {
                    $(this).removeClass('shadow-lg');
                }
            );
        });
	 
  }
    render() {

    return (
		<div className="row mb-4">{
			this.state.APIs.map((APIs, i) => (
			<div key={i}className="col-sm-6 py-2 col-lg-4">
				<div key={i}class="card h-100">
					<div key={i}class="card-body">
						<h5 key={i}class="card-title">
						<i class={APIs.icon}></i> {APIs.title}</h5>
						<p key={i} class="card-text">{APIs.descriptions}</p>
					</div>
					<div class="card-detail mx-3 "> 
					    <ul class = "list-inline "></ul>
					</div>
				</div>
			 </div>
			))
		}
        
       </div>
    );
  }
}

export default APIsBox;
