import React, { Component } from 'react';
import styled from 'styled-components';
import $ from 'jquery';
import axios from 'axios';
const APIs_URL = 'final-data.json';
class AppList extends React.Component {
	
	constructor(props) {
    super(props);
    this.state = {
      users: [],
    isLoading: true,
    errors: null
    };
  }
	
 componentDidMount() {
   axios
    .get("new.json")
    .then(response =>
      response.data.results.map(user => ({
		apptitle:`${user.apptitle}`,
		description:`${user.descriptions}`,
		icon:`${user.icon}`,
        link: `${user.link.intro} ${user.link.guide} ${user.link.reference}`,
        
      }))
    )
    .then(users => {
      this.setState({
        users,
        isLoading: false
      });
    })
    .catch(error => this.setState({ error, isLoading: false }));
}

  render() {
  const { isLoading, users } = this.state;
  return (
    <React.Fragment>
     
      <div className="row mb-4">
        {!isLoading ? (
          users.map(user => {
            const { apptitle,icon,description,username,link} = user;
			
            return (
                <div className="col-sm-6 py-2 col-lg-4">
			        <div className={`card h-100`}>
				        <div className="card-body">
				            <h5 className="card-title">< i className={icon}></i>{apptitle}</h5>
							<p className="card-text">{description}</p>
						</div>  
						<div className="card-detail mx-3 ">
						{link}
						</div>
					</div>
				</div>
            );
          })
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </React.Fragment>
  );
}
}
export default AppList;
//https://css-tricks.com/using-data-in-react-with-the-fetch-api-and-axios/
