import React, { Component} from 'react';
import $ from 'jquery';
import Intro from './components/Intro';
//import data from "../data"; 
import axios from 'axios';
class Api extends Component{
	constructor(props) {
    super(props);
		this.state = {
		  error: null,
		  isLoaded: false,
		  items: []
		};
    }
	componentDidMount() {
		$(document).ready(function() {
            $( ".card" ).hover(
                function() {
                    $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
                }, function() {
                    $(this).removeClass('shadow-lg');
                }
            );
        });
	fetch("reactjs.json")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
       (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
		  
		  
		  
	}
	render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {items.map(item => (
            <li key={item.title}>
              {item.title} {item.price}
            </li>
          ))}
        </ul>
      );
    }
  }
}
export default Api;
