import React, { Component } from 'react';
export default class Car extends Component{
	
	constructor(props) {
		super(props);
		this.state = {
				brand: "Ford",
				model: "Mustang",
				color: "red", 
				year: 1964,
				hover:false
			};
    }
	changeColor = () => {
		this.setState({color: "blue"});
    }
	toggleHover=()=>{
		
		this.setState({hover: !this.state.hover})
	}
	
	
	render() {
		var linkStyle;
		
		if (this.state.hover) {
		 linkStyle = {color: '#ed1212',cursor: 'pointer'}
		} else {
		 linkStyle = {color: '#000'}
		}
    return (
      <div>
        <h1>My Car {this.state.brand} {this.state.model}  {this.state.color}</h1>
		
		<p style={linkStyle} onMouseEnter={this.toggleHover} onMouseLeave={this.toggleHover}>Test {this.state.hover}</p>
		<button
          type="button"
          onClick={this.changeColor}
        >Change color</button>
		
      </div>
    );
	}
}
