            $( ".card" ).hover(
                function() {
                    $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
                }, function() {
                    $(this).removeClass('shadow-lg');
                }
            );
        });
	}
	
	render(){
		return (
			 <div className="row mb-4">
			 {
				<div>
        <h1>Jobs!</h1>
        {this.state.jobs.map(function(job) {
          return (
            <div key={job.id} className="job">
              <a href={job.url}>
                {job.company_name}
