import React, { Component } from 'react';
import $ from 'jquery';
import axios from 'axios';
const API_URL = 'final-data.json';

class ApiBox extends Component {
  state = {
    api: []
  }
  componentDidMount() {
    //const url = `${API_URL}/api/`;
	const url = `${API_URL}`;
    axios.get(url).then(response => response.data)
    .then((data) => {
      this.setState({ api: data })
      //console.log(this.state.api)
     })
	 
	 $(document).ready(function() {
            $( ".card" ).hover(
                function() {
                    $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
                }, function() {
                    $(this).removeClass('shadow-lg');
                }
            );
        });
	 
  }
    render() {

    return (
       <div className="row mb-4">
        {
			this.state.api.map((api, i) => (
		<div className="col-sm-6 py-2 col-lg-4">
          <div key={i} className="card h-100">
           <div key={i} className="card-body">
				<h5 className="card-title"><i class={api.icon}></i>{api.title}</h5>
			   <p className="card-text">{api.descriptions}</p>
              <h6 className="card-subtitle mb-2 text-muted">
              {api.email}             
              </h6>
            </div>
          </div>
		  <div className="card-detail mx-3 "> 
		  <ul class = "list-inline ">
							<li><a  href={api.link} class="text-dark no-underline text-capitalize footer-link-icon">Intro <span class="position-relative"><i class="fa fa-chevron-right float-right" ></i></span></a></li>
							<hr/>
							<li><a  href={api.link} class="text-dark no-underline text-capitalize">Developer's guide <span class="position-relative"><i class="fa fa-chevron-right float-right"></i></span></a></li>
							<hr/>
							<li><a   href={api.link} class="text-dark no-underline text-capitalize"> API reference<span class="position-relative"><i class="fa fa-chevron-right float-right"></i></span></a></li>
						</ul>
		  </div>
		  </div>
		  
        ))}
        
       </div>
    );
  }
}

export default ApiBox;
